<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="javascript:void(0);" class="on">快店优惠列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/discount_add')}','添加优惠',480,260,true,false,false,addbtn,'add',true);">添加优惠</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>类别</th>								<th>满足金额</th>								<th>优惠金额</th>								<th>使用状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($discount_list)">								<volist name="discount_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td><if condition="$vo['type'] eq 0">新单<elseif condition="$vo['type'] eq 1" />满减<else />配送</if></td>										<td>{pigcms{$vo.full_money}</td>										<td>{pigcms{$vo.reduce_money}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/discount_edit',array('id' => $vo['id'], 'frame_show'=>true))}','查看优惠信息',480,260,true,false,false,false,'detail',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/discount_edit',array('id' => $vo['id']))}','编辑优惠信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>