<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Shop/shop')}" class="on">{pigcms{:L('_BACK_AREA_LIST_')}</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Shop/shop')}" method="get">							<input type="hidden" name="c" value="Shop"/>							<input type="hidden" name="a" value="shop"/>							{pigcms{:L('_BACK_STORE_NAME_')}: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>{pigcms{:L('_BACK_MER_NAME_')}</th>								<th>{pigcms{:L('_BACK_STORE_NAME_')}</th>								<th>{pigcms{:L('_BACK_SERVICE_AREA_')}</th>								<th>{pigcms{:L('_BACK_MER_PHONE_')}</th>								<th>{pigcms{:L('_BACK_STORE_PHONE_')}</th>								<th>{pigcms{:L('_BACK_SETUP_TIME_')}</th>								<th class="textcenter">{pigcms{:L('_BACK_CZ_')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($order_list)">								<volist name="order_list" id="vo">									<tr>										<td>{pigcms{$vo.merchant_name}</td>										<td>{pigcms{$vo.store_name}</td>										<td><if condition="$vo['delivery_radius'] gt 0">{pigcms{$vo.delivery_radius|floatval} {pigcms{:L('_BACK_MILES_')}<else /><b style="color:red">{pigcms{:L('_BACK_UNSET_')}</b></if></td>										<td>{pigcms{$vo.merchant_phone}</td>										<td>{pigcms{$vo.store_phone}</td>										<td>{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/shop_edit',array('store_id' => $vo['store_id']))}','{pigcms{:L(\'_BACK_EDIT_AREA_\')}',680,480,true,false,false,editbtn,'edit',true);">{pigcms{:L('_BACK_STORE_SET_')}</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>